"use strict";

module.exports = {
  multiTap: function multiTap(total, cb, timeout) {
    var tapCount = 0;
    var tapTimeout;

    return {
      onTouchStart: function onTouchStart(e) {
        clearTimeout(tapTimeout);
        tapCount++;

        if (tapCount == total) {
          tapCount = 0;
          cb(e);
        } else {
          tapTimeout = setTimeout(function () {
            tapCount = 0;
          }, timeout || 500);
        }
      }
    };
  }
};